import { useState, useEffect } from 'react';
import styles from "./info-board.module.scss";
import generateNumberPlate from "../../utils/number-plates-generator";

import moment from 'moment';

import carUrl from "../../assets/car.png";
import carUrl1 from "../../assets/car1.png";
import mcarUrl from "../../assets/mcar.png";
import mcarUrl1 from "../../assets/mcar1.png";
import lcarUrl from "../../assets/lcar.png";
import lcarUrl1 from "../../assets/lcar1.png";

let cars = [
  { size: 'small',
    imgFront: carUrl,
    imgBack: carUrl1
  },
  { size: 'medium',
  imgFront: mcarUrl,
  imgBack: mcarUrl1
},
{ size: 'large',
imgFront: lcarUrl,
imgBack: lcarUrl1
}
]

export const SlotInfoBoard = ({ handleUpdate, handleClose, data}) => {
  const [values, setValues] = useState({nearestEntrance: [], slotNum: '', slotType: 'small'});

  const handleChanges = prop => event => {
    setValues({...values, [prop]: event.target.value});
}

const handleGenerate = () => {
    let np = generateNumberPlate();
    setValues({...values, numberPlate: np});
}


let vals =  localStorage.getItem('parkingData');

let entrance = Array.from(Array(data.nearestEntrance).keys());
let imgs = data.img ? data.img : {};

useEffect(() => {
  setValues(data);
},[data])

  return (
    <>
    <div className={`${styles.infoBoard} ${styles.mt50}`}>
      <span className={styles.closeBtn} onClick={handleClose}>x</span>
      <h3>SLOT DETAILS.</h3>
      <br/>
      <select
            placeholder={`Enter your desired size`}
            value={values.entry}
            onChange={(e) => setValues({...values, entry: e.target.value})}
          >
                <option className='optionTitle' value="">ENTRY POINTS</option>
             {entrance.map(a => {
              return (
                <option key={a} value={a}>Entrance - {a + 1}</option>
              )
             })}

            </select>
            <select
            placeholder={`Select and Order by nearest entrance.`}
            value={values.slotType}
            onChange={(e) => { 
              let img = cars.find(a => a.size === e.target.value);
              setValues({...values, slotType: e.target.value, imgFront: null, imgBack: null, size: '', ...img})
            }}
          >
              <option className='optionTitle' value="">Select Entrances</option>
             {values.nearestEntrance.map(a => {
              return (
                <option key={a.size} value={a.size}>{String(a.size).toUpperCase()}</option>
              )
             })}

            </select>
        <br/>
     <div className={styles.carCard}>
     {values.imgFront && <img  src={values.imgFront} alt={`${String(values.size).toUpperCase()} CAR`} />
            }</div>
             <div className={styles.controls}>
      <button type="submit" onClick={() => handleUpdate(values)} className={`${styles.submitBtn} ${(!values.slotNum || !values.nearestEntrance || !values.slotType) ? styles.disabledBtn : '' }`} disabled={!values.numberPlate || !values.entry || !values.size} >UPDATE</button>
    </div>
            </div>
    </>
  );
};
